INSERT INTO `#__wub_armes` VALUES(1, 'avenger.jpg', 10, 2, 3, 1, 1, 300, 25, 'Arme pour petites attaques.\r\nPeu précise mais légère le contient un chargeur de 10 Munitions.', 'Avenger', 1758, 10, 0, 0);
INSERT INTO `#__wub_armes` VALUES(2, 'avenger_mini.jpg', 10, 3, 2, 1, 1, 400, 30, 'Plus petite que sa grande soeur mais elle est très rapide pour les attaque de débutant.', 'Avenger mini', 1759, 9, 0, 0);
INSERT INTO `#__wub_armes` VALUES(3, 'keltec_kfb_folding_bayonet.jpg', 30, 1, 1, 1, 1, 50, 1, 'Mieux vaut ça que c\'est main. Tu peux avoir 30 recharges pour tes lames.', 'couteau keltec', 1821, 99, 0, 0);
INSERT INTO `#__wub_armes` VALUES(4, 'beretta_GoldRushCarbine.jpg', 5, 4, 3, 1, 1, 500, 32, 'Attention au recul, c\'est pas une arme pour les petites filles.', 'Beretta Gold Rush Ca', 1786, 5, 0, 0);
INSERT INTO `#__wub_armes` VALUES(5, 'beretta_Laramie_blue.jpg', 8, 5, 5, 1, 1, 750, 40, 'Aussi bien en attaque qu\'en défense, cette arme est fiable avec son chargeur 8 coups.', 'Beretta Laramie blue', 1821, 4, 0, 0);
INSERT INTO `#__wub_armes` VALUES(6, 'ber_92f.jpg', 7, 6, 7, 1, 1, 800, 45, 'Une parfaite seconde arme, elle est rapide, précise par contre son chargeur reste petit.', 'Beretta 92 fs', 1758, 6, 0, 0);
INSERT INTO `#__wub_armes` VALUES(7, 'ber_92gsd.jpg', 11, 6, 7, 1, 1, 900, 46, 'La petite soeur de 92 fs avec un chargeur haute-capacité de 11 balles.', 'Beretta 92 gsd', 1759, 7, 0, 0);
INSERT INTO `#__wub_armes` VALUES(8, 'ber_eliteII.jpg', 10, 10, 9, 1, 1, 1200, 49, 'Une arme pour la petite attaque, peu précise mais légère. Elle a une prise en main très rapide.', 'Beretta Elite', 1786, 9, 2, 0);
INSERT INTO `#__wub_armes` VALUES(9, 'ber_stampede_deluxe_4.5.jpg', 6, 14, 10, 1, 1, 1300, 53, 'Un petit bébé qui peut faire très mal. Pour toutes les personnes qui adore les westerns.', 'Beretta stampede del', 1821, 1, 2, 0);
INSERT INTO `#__wub_armes` VALUES(10, 'ber_92_steel1.jpg', 5, 15, 15, 1, 1, 1600, 60, 'Le légendaire pistolet Beretta M 92 FS a conquis à juste titre la confiance des unités spéciales, des forces armées et des forces de police du monde entier.', 'beretta M 92FS INOX', 1758, 3, 3, 0);
INSERT INTO `#__wub_armes` VALUES(11, 'ber_Stampede_Inox.jpg', 7, 15, 14, 1, 1, 1650, 73, 'Que du bonheur, rien de mieux vous dirons les experts des stands de tir. Attention au recule.', 'Beretta Stampede Ino', 1759, 4, 3, 0);
INSERT INTO `#__wub_armes` VALUES(12, 'ber_neos_inox_6.jpg', 6, 17, 18, 1, 1, 2350, 79, 'Haute gamme pour une petite arme, rarement enrayer, vous pouvez même prendre votre douche avec. Très belle arme de main.', 'Beretta neos inox 6', 1786, 5, 4, 0);
INSERT INTO `#__wub_armes` VALUES(13, 'ber_stampede_nick_7.5.jpg', 7, 17, 17, 1, 1, 2400, 86, 'Produit de luxe, elle a une défense qui est au niveau de son attaque. Rien de mieux pour faire de gros trous.', 'Beretta stampede nic', 1821, 1, 4, 0);
INSERT INTO `#__wub_armes` VALUES(14, 'mitch_1911_blue.jpg', 12, 17, 16, 1, 1, 2500, 94, 'Arme très légère, très dur à détecter car une grosse partie de l\'arme est en bois. ', 'Mitch 1911 bois', 1758, 4, 5, 0);
INSERT INTO `#__wub_armes` VALUES(15, 'bush_c15p97.jpg', 8, 20, 19, 1, 1, 2640, 103, 'Une arme qui tire par a coups mais qui loupe rarement sa cible. Il faut avoir une dizaine de pts d\'expérience pour pouvoir s"en servir.', 'Bush', 1759, 3, 10, 0);
INSERT INTO `#__wub_armes` VALUES(16, 'spring_PC9105L.jpg', 9, 19, 19, 1, 1, 2750, 106, 'Arme utilisé surtout pour les commandos, elle possède une visée lacer très précise.', 'Spring PC 9105 L', 1786, 1, 11, 0);
INSERT INTO `#__wub_armes` VALUES(17, 'p22_package.jpg', 9, 20, 20, 1, 1, 2890, 116, 'Une arme très discret par sa taille mais aussi par son silencieux haute gamme.', 'P 22 package', 1821, 4, 11, 0);
INSERT INTO `#__wub_armes` VALUES(18, 'Thompson_M1_SBR.jpg', 10, 23, 23, 1, 1, 3020, 128, 'Une bonne utilisation de cette arme peut vous rendre dangereux. Attention de bien la déclarer aux autorités.', 'Thompson M1 SBR', 1759, 9, 13, 0);
INSERT INTO `#__wub_armes` VALUES(19, 'bush_9mm_carb.jpg', 15, 25, 23, 1, 1, 3400, 135, 'Arme assaut, les formes d\'intervention rapide l\'adore. C\'est pour les fans de tir rapide.', 'Bush 9mm carb', 1758, 2, 14, 0);
INSERT INTO `#__wub_armes` VALUES(20, 'century_ri1190-x.jpg', 12, 26, 27, 1, 1, 3540, 139, 'Fusil d\'assaut, convient sur n\'importe quel terrain. Une bonne capacité chargeur.', 'Century RI 1190-x', 1759, 2, 15, 0);
INSERT INTO `#__wub_armes` VALUES(21, 'fn_fnp9m.jpg', 15, 27, 27, 1, 1, 3760, 142, 'Une arme intermédiaire pour les malades qui aiment faire de gros trous dans les têtes.', 'FN fnp 9m', 1786, 4, 23, 0);
INSERT INTO `#__wub_armes` VALUES(22, 'bush_9mm_pistol.jpg', 10, 31, 30, 1, 1, 3860, 151, 'Avec son canon scié, il est très bien pour l\'auto défense. Il est utilisé par les services secret.', 'Bush 9mm pistol', 1759, 5, 25, 0);
INSERT INTO `#__wub_armes` VALUES(23, 'century_ki1042.jpg', 20, 30, 30, 1, 1, 3999, 158, 'Mitrailleuse très performante mais très lourde, c\'est surtout son chargeur 20 coups qui est intéressant. Faut connaître un peu les armes avant de se lancer avec ça.', 'Century ki 1042', 1821, 1, 26, 0);
INSERT INTO `#__wub_armes` VALUES(24, 'century_ki1115.jpg', 3, 45, 40, 1, 1, 6000, 174, 'Sur pied cette arme fait beaucoup de bruit quand on tire mais la personne en face n\'aura pas le temps de l\'entendre.', 'Century KI 1115', 1758, 2, 67, 0);
INSERT INTO `#__wub_armes` VALUES(25, 'HK_53_Collapse2.jpg', 15, 37, 37, 1, 1, 4560, 183, 'Une arme de guerre pour le fou des petites mitraillettes.', 'HK 53 Collapse2', 1821, 3, 27, 0);
INSERT INTO `#__wub_armes` VALUES(26, 'cyclone.jpg', 15, 39, 39, 1, 1, 4670, 190, 'Avec son silencieux, ce sniper ne loupera pas sa cible. On ne vous voit pas mais vous ne pouvez pas louper votre cible.', 'Cyclone', 1786, 1, 26, 0);
INSERT INTO `#__wub_armes` VALUES(27, 'cz_durango.jpg', 6, 47, 39, 1, 1, 5630, 199, 'Un fusil qui ne laisse pas indifférent quand on l\'a devant sois. Attention cette arme n\'a pas beaucoup de capacité.', 'CZ durango', 1821, 7, 50, 0);
INSERT INTO `#__wub_armes` VALUES(28, 'rom_wasr10fold_drum.jpg', 10, 46, 40, 1, 1, 4920, 204, 'Une ancienne arme de la guerre, qui est souvent vendu sous le manteau.Vielle mais toujours efficace.', 'Rom wasr 10 fold', 1759, 3, 28, 0);
INSERT INTO `#__wub_armes` VALUES(29, 'LR-300-SR-A.jpg', 13, 32, 35, 1, 1, 5110, 219, 'Attention messieurs dames, ca va faire mal, il y a du lourd. 20 Kg de furie. Petit gabarit ne pas utiliser.', 'LR 300 SR A', 1759, 2, 30, 0);
INSERT INTO `#__wub_armes` VALUES(30, 'raptor.jpg', 13, 36, 32, 1, 1, 5290, 225, 'La puissance, la discrétion et la rapidité dans une petite arme, voilà ce qu\'est le Raptor. Pour ne pas se faire ennuyer, il suffit de l\'avoir.', 'Raptor', 1758, 5, 30, 1);
INSERT INTO `#__wub_armes` VALUES(31, 'fn_a4_system.jpg', 10, 38, 36, 1, 1, 5360, 234, 'Sur pied, cette arme d\'une précision rare peut faire de très gros dégâts de loin mais aussi de près.', 'FN A4 system', 1821, 1, 32, 0);
INSERT INTO `#__wub_armes` VALUES(32, 'joeken_black104S.jpg', 14, 36, 36, 1, 1, 5570, 247, 'Arme utilisé surtout par les terroristes pour sa capacité de munitions et pour sa discrétion.', 'Joeken black 104 S', 1758, 4, 33, 0);
INSERT INTO `#__wub_armes` VALUES(33, 'LR-300-ML-N.jpg', 12, 42, 42, 1, 1, 5940, 253, 'Comme dans les jeux vidéos et qu\'on adore voir du sang sur tous les murs rien de mieux. Voilà à quoi correspond cette arme.', 'LR 300', 1758, 3, 61, 0);
INSERT INTO `#__wub_armes` VALUES(34, 'g36.jpg', 10, 50, 49, 1, 1, 6220, 261, 'Une arme très confortable sur le dos, vous pourrez faire plusieurs Km sans avoir à douter d\'elle.', 'G 36', 1786, 2, 73, 0);
INSERT INTO `#__wub_armes` VALUES(35, 'spring_scout_socom.jpg', 10, 50, 50, 1, 1, 6354, 276, 'Genre d\'arme qui commence a monter dans l\'expérience. Elle fera des ravages sur vos ennemis.', 'Spring scout socom', 1786, 2, 82, 0);
INSERT INTO `#__wub_armes` VALUES(36, 'LR-300-ML-A.jpg', 10, 52, 52, 1, 1, 6585, 281, 'Cette arme fait des flammes quant elle tire qui sont aussi grandes qu\'elle.', 'LR 300 ML A', 1821, 2, 88, 0);
INSERT INTO `#__wub_armes` VALUES(37, 'spg_sa9805.jpg', 6, 60, 58, 1, 1, 6723, 289, 'Avec un camouflage comme celui que propose cette arme vous n\'aurez pas de soucis a vous faire discret dans le décor.', 'SPG sa 9805', 1758, 1, 94, 0);
INSERT INTO `#__wub_armes` VALUES(38, 'keltec_su-16_d9.jpg', 12, 57, 57, 1, 1, 6920, 294, 'Arme qui demande une expérience d\'au moins 102 pts, il faut au moins 2-3 petits jours pour pouvoir se l\'offrir.', 'Keltec SU 16', 1786, 8, 102, 0);
INSERT INTO `#__wub_armes` VALUES(39, 'LR-300-SR-N.jpg', 12, 60, 60, 1, 1, 7140, 299, 'Arme pour moyenne attaque, moyennement lourd elle fera de vous un homme, un vrai.', 'LR 300 SR', 1786, 2, 104, 0);
INSERT INTO `#__wub_armes` VALUES(40, 'tactical_case.jpg', 10, 65, 65, 1, 1, 7360, 320, 'Le kit parfait pour ne plus être emmerdé par les autres joueurs. Un chargeur confortable vous permettra de faire de longs voyages.', 'Tactical case', 1821, 3, 110, 0);
INSERT INTO `#__wub_armes` VALUES(41, 'mk25_smwes.jpg', 15, 65, 62, 1, 1, 7440, 330, 'Vendu avec son étuit il vous permettra de voyager sans attirer l\'attention des autres joueurs.', 'MK 25 smwes', 1759, 1, 124, 0);
INSERT INTO `#__wub_armes` VALUES(42, 'daly_field_hunter_blk.jpg', 6, 70, 68, 1, 1, 7630, 350, 'Poids lourd dans sa catégorie, ce fusil enverra directement vos ennemis au paradis.', 'Daly field hunter', 1758, 3, 160, 0);
INSERT INTO `#__wub_armes` VALUES(43, 'fn_civil_p90.jpg', 12, 67, 67, 1, 1, 7780, 370, 'La moustiquaire des armes, elle tire tellement vite qu\'on ne se rends pas compte que le chargeur est vide.', 'FN civil P90', 1758, 4, 180, 0);
INSERT INTO `#__wub_armes` VALUES(44, 'sks_yugo.jpg', 12, 70, 70, 1, 1, 7910, 385, 'Une arme d\'origine yugo, elle détient au bout de son canon une lame très dangereuse au corps à corps.', 'SKS', 1821, 10, 200, 0);
INSERT INTO `#__wub_armes` VALUES(45, 'Thompson_1927_A-1_SBR.jpg', 15, 71, 69, 1, 1, 8230, 405, 'Arme créée en 1927, elle reste la meilleur mitrailleuse portable pour un poids de moins de 15 Kg', 'Thompson A-1 SB', 1758, 2, 250, 0);
INSERT INTO `#__wub_armes` VALUES(46, 'fn_a3g.jpg', 3, 89, 84, 1, 1, 8499, 450, 'Le fleuron des snipers vos ennemis ne penseront plus qu\'a une chose, ne pas vous croiser de près ou de loin.', 'FN A3 g', 1786, 1, 300, 0);
INSERT INTO `#__wub_armes` VALUES(47, 'navy.jpg', 15, 75, 73, 1, 1, 8679, 475, 'Ce n\'est pas une arme mais 2 en une, ce qui vous rends plus puissant et plus rapide.', 'Navy', 1786, 2, 300, 0);
INSERT INTO `#__wub_armes` VALUES(48, 'm4.jpg', 15, 75, 75, 1, 1, 8920, 489, 'Arme de moyen calibre, précise et peu légère avec un chargeur 15 munitions, elle   vous aidera avec facilité à faire pleins de trous dans les corps de vos ennemis.', 'M4', 1821, 3, 370, 0);
INSERT INTO `#__wub_armes` VALUES(49, 'tikka_Rifles.jpg', 5, 85, 82, 1, 1, 9224, 500, 'Rare sont ceux qui arrive a en avoir un. Faut s\'y connaître dans le jeu.', 'Tikka Rifles', 1786, 1, 400, 0);
INSERT INTO `#__wub_armes` VALUES(50, 'fn_f2000d.jpg', 10, 82, 82, 1, 1, 9470, 550, 'Prototype très rare, vous devez le mériter pour l\'avoir. Rien de plus sur la doc de cette arme.', 'FN F 2000', 1759, 1, 400, 0);
INSERT INTO `#__wub_armes` VALUES(51, 'fn_police_sg.jpg', 7, 85, 87, 1, 1, 9790, 600, 'Arme de marché noir, uniquement disponible pour les forces de l\'ordre. Attention si un flic vous voit avec ça, c\'est chaud pour votre cul.', 'FN police sg', 1758, 1, 600, 0);
INSERT INTO `#__wub_armes` VALUES(52, 'fn_tactical_shotgun.jpg', 8, 83, 95, 1, 1, 10500, 700, 'Mini lance-flamme sous le canon, après avoir tué votre ennemis, pourquoi ne pas faire un barbecue? ', 'FN Tactical shotgun', 1759, 1, 650, 0);
INSERT INTO `#__wub_armes` VALUES(53, 'fn_hecate.jpg', 5, 90, 95, 1, 1, 12040, 800, 'Arme de légende rare son les personnes à l\'avoir vu en vrai.', 'FN hecate', 1821, 1, 1000, 0);
INSERT INTO `#__wub_armes` VALUES(54, 'fn_303.jpg', 7, 100, 95, 1, 1, 15430, 900, 'Lance grenade, avec ça vous êtes sur de pouvoir passer ou vous voulez.', 'FN 303', 1759, 2, 1700, 0);
INSERT INTO `#__wub_armes` VALUES(55, 'sig_sa5.jpg', 10, 99, 98, 1, 1, 17640, 1000, 'Arme la plus puissante du jeu, elle est destinée aux meilleurs des meilleurs. Serez vous capable de vous la fournir ?', 'SIG sa5', 1759, 2, 2500, 1);
INSERT INTO `#__wub_armes` VALUES(56, 'bajjoka.jpg', 3, 100, 100, 1, 1, 25400, 4000, 'Pour les meilleurs des meilleurs.', 'Bazooka', 1821, 1, 3500, 0);
INSERT INTO `#__wub_articles` VALUES(1, '<p>{joueur} a encore fait parler de lui. Et oui, c\'est en rentrant chez lui ({position}) que {objectif} c\'est rendu compte qui lui manquait environs {argent}, Apr&egrave;s quelques jours d\'enqu&ecirc;te, nous savons que {joueur} a agit seul pour lui soutirer, via la technique des 9 rennes. Faite attention &agrave; vous, je pense que {joueur} va faire encore parler de lui.</p>', 7);
INSERT INTO `#__wub_articles` VALUES(2, '<p>L\'homme, plac&eacute; en garde &agrave; vue, aurait commis d&rsquo;autres faits (agression, vol, tentatives de viol) dans la r&eacute;gion vers {position}. Des analyses sont en cours afin de confirmer un rapprochement entre ces diff&eacute;rentes affaires. L\'homme serait connu des services de police et souffrirait de probl&egrave;mes psychiatriques selon le procureur-adjoint. L&rsquo;agresseur pr&eacute;sum&eacute; est &acirc;g&eacute; de 33 ans et domicili&eacute; pr&egrave;s de Mafiajob. La liste de faits &agrave; relier serait longue. L\'homme aurait vol&eacute; un 4x4 &quot;, vendredi dernier. Ce v&eacute;hicule a &eacute;t&eacute; retrouv&eacute; sur les lieux du crime&quot; selon le procureur-adjoint. Depuis son viol et son agression physique, {joueur} &agrave; toujour du mal &agrave; s\'en remettre.</p>', 6);
INSERT INTO `#__wub_articles` VALUES(3, '<p>Une affaire de famille? Et bien non, {joueur} n\'est pas le p&egrave;re de : {objectif}. La preuve il vient de lui refaire la tronche, d\'ailleur : {objectif} vous le dira de lui m&ecirc;me !!! A mon avis, on va encore entrendre parler d\'eux!!! Pour toutes les personne qui adore ce genre d\'histoire, je vous invite &agrave; vous rendre en {position}.</p>', 5);
INSERT INTO `#__wub_articles` VALUES(4, '<p>{objectif} vient de se faire refaire le visage apres l accrochage qu il a eu avec {joueur} en {position}, Il n\'avait pas le choix avec sa tronche d\'&Eacute;l&eacute;phant Man... Les m&eacute;decin ont fais du super boulot, d&eacute;sormais il ressemble plus &agrave; la souris dans Tom et Jerry... {joueur} a gagn&eacute; {argent} et {objectif} a perdu sa belle tronche.</p>', 5);
INSERT INTO `#__wub_articles` VALUES(5, '<p>{joueur} a &eacute;t&eacute; vu en train de courir pr&egrave;s de {position} o&ugrave;  {objectif} a &eacute;t&eacute; vu pour la derni&egrave;re fois par un habitant de Mafiajob, {objectif}  a &eacute;t&eacute; retrouv&eacute; compl&eacute;tement d&eacute;sint&eacute;gr&eacute; si l\'on peut dire... L\'ambulance arriv&eacute; sur les lieux  du crime ont bien gal&eacute;r&eacute; pour r&eacute;cup&eacute;rer tous les petits bouts r&eacute;partis un peu partout dans la ruelle. D\'apr&egrave;s les t&eacute;moins, {joueur} serait parti avec {argent} qu\'il aurait piqu&eacute; &agrave; {objectif} avant de le laisser au bord de la route.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(6, '<p>{objectif} s\'est fait prendre par {joueur} dans un coin de rue en {position}. Le pauvre  {objectif} sortant de l\'hopital pour fracture du bassin s\'est fait sodomiser par {joueur}. Des infermi&egrave;res ont retouv&eacute; {objectif} le pantalon baiss&eacute; et le cul pleins de sang. Je pense que {objectif} va avoir du mal &agrave; s\'en remettre.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(7, '<p>En voulant aller t&eacute;moigner au tribunal {objectif} s\'est fait attaqu&eacute; sur la route qui donne sur {position}, prit entre 2 camions poubelle, 5 hommes l\'ont sorti violement de sa bagnole, puis l\'ont abattu d\'une seul balle entre les 2 yeux...  Les t&eacute;moins ayant assit&eacute;s &agrave; la sc&egrave;ne ont tr&egrave;s clairement reconnu {joueur} comme &eacute;tant l\'un des cinq hommes. Il para&icirc;t m&ecirc;me que {joueur} lui aurait fait les poches apr&egrave;s l\'avoir tu&eacute; et serait parti avec {argent}.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(8, '<p>{objectif} &agrave; encore &eacute;t&eacute; victime d\'un vole (le pauvre, il a vraiment pas de bol)! A priori, c\'est pas la premiere fois que {joueur} le prends pour sa victime !!! {objectif} est retourn&eacute; chez sa m&egrave;re car {joueur} lui a m^&ecirc;me piqu&eacute; ses clefs. La facture pour {objectif} est de {argent} dur pour un ch&ocirc;meur!</p>', 9);
INSERT INTO `#__wub_articles` VALUES(9, '<p><input width="50" vspace="3" hspace="3" height="45" align="right" type="image" src="./images/stories/trottoir.jpg" longdesc="undefined" />C\'est avec stup&eacute;faction que l\'on a retrouv&eacute; le corps de {joueur} allong&eacute; sur le sol en {position} avec une flute traversi&egrave;re enfonc&eacute;e dans cul et le visage lasser&eacute; par un cutter. La police soup&ccedil;onne {joueur} comme le meurtrier .Les enqu&ecirc;tes se poursuivent...</p>', 2);
INSERT INTO `#__wub_articles` VALUES(10, '<p>Que vouliez vous que {objectif} puisse faire avec un flingue sur la tronche. D apres sa d&eacute;position, on pourra remarquer que l\'agresseur est connu des services de police et qu il s agissait de {joueur}, reput&eacute; pour sa violence et sa fa&ccedil;on de mettre ses victimes &agrave; poil, pieds et mains li&eacute;s. R&eacute;sultat des courses {objectif} n\'a plus que ses yeux pour pleurer car il vient de perdre {argent}</p>', 10);
INSERT INTO `#__wub_articles` VALUES(11, '<p>La police enqu&ecirc;te sur l\'agression de {objectif} retrouv&eacute; dans une poubelle en {position} avec une oreille arrach&eacute;e. Les premiers &eacute;l&eacute;ments de l\'enqu&ecirc;te se rapporte &agrave; {joueur} qui est connu pour &ecirc;tre violent voir sado et qui aime se faire des colliers avec les oreilles de ses victimes. D\'apr&egrave;s son emplois du temps {joueur} n\'&eacute;tait pas chez lui vers : {date}.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(12, '<p>O&ugrave; &eacute;tais la police? Et oui ! {objectif} vient de se faire arracher son sac &agrave; main pendant qu\'il se promenait au bord de la route vers {position}, nous n\'avons pas plus de d&eacute;tail sur l\'agresseur mais la question est : Pourquoi un homme avec un sac &agrave; main au bord de la route le {date} ?</p>', 9);
INSERT INTO `#__wub_articles` VALUES(13, '<p>{joueur} vient d\'&eacute;chapper &agrave; la police (&agrave; priori, il court plus vite qu eux). Effectivement, l\'ayant vu entrain de voler {objectif} la police s\'est mis &agrave; lui courrir apr&egrave;s mais sans aucun r&eacute;sultat !!!! {joueur} court toujours dans la nature! Il a piqu&eacute; {argent} au pauvre {objectif}.</p>', 7);
INSERT INTO `#__wub_articles` VALUES(14, '<p>{joueur} n\'a pas eu de mal a voler {objectif}. Effectivement, il a suffit a {joueur} d utilis&eacute; un stylo pour faire croire a {objectif} qu\'il avait un pistole braqu&eacute; dans le dos. je vous laisse imaginer la suite. Faut savoir que {joueur} est reparti vers {position} avec {argent} en poche.</p>', 9);
INSERT INTO `#__wub_articles` VALUES(15, '<p>Pour une raison que la police ignore {joueur} se serait ru&eacute; sur {objectif} et lui aurait fait passer un sale quart d heure. La police aurai retrouver  {objectif} gisant sur le sol en {position} les bras et les jambes arrach&eacute;s. Pourquoi {joueur} a fait &ccedil;a? Personne ne sait, peut-&ecirc;tre pour les {argent} qu\'il avait sur lui.</p>', 5);
INSERT INTO `#__wub_articles` VALUES(16, '<p>On vient de retrouver le corps calcin&eacute; de {objectif} dans une benne &agrave; ordure qui se trouve en {position}. {objectif} avait des comptes &agrave; rendre au joueur {joueur}, mais &ccedil;a n\'a pas dut se passer comme il le voulait... On ne rigole pas avec ces gens l&agrave;!</p>', 1);
INSERT INTO `#__wub_articles` VALUES(17, '<p>Victime de 47 coups de couteau, le corps de {joueur} a visiblement eu affaire &agrave; la vengeance de {objectif}, &agrave; qui, il devait une somme importante, &agrave; priori {argent}. {objectif}, surnomm&eacute; le Boucher, a encore fait parler de lui... Faites attention vers {position}.</p>', 6);
INSERT INTO `#__wub_articles` VALUES(18, '<p>Apres quelques ann&eacute;es de cavale  {objectif} c\'est fait arreter en {position} de mani&egrave;re plus que forte en a d&eacute;duit la police .. Effectivement, on le retrouva le lendemain matin, la bouche explox&eacute;e dans une ruelle.... Des indices laissent &agrave; penser que {joueur} peut etre le suspect num&eacute;ro 1.</p>', 5);
INSERT INTO `#__wub_articles` VALUES(19, '<p>Enfin le c&eacute;l&egrave;bre  {joueur} vient de ce faire shooter par le G.I.G.N. ce matin en {position}. Alors qu\'il sortais de {objectif} en {position}, Tout le monde sait qu\'il est chaud de braquer ce b&acirc;timent dans ces conditions surtout le {objectif}.</p>', 4);
INSERT INTO `#__wub_articles` VALUES(20, '<p>Un voisin nous a racont&eacute; l\'histoire et voil&agrave; ce qu\'il en resort : {objectif} n\'a pas eu le temps de comprendre, car il sortait d\'un b&acirc;timent vers {position} dans son quartier, quand deux voitures de la B.A.C sortent de nul part, s\'arr&ecirc;tent, l\'attrapent, et le forcent &agrave; monter dans leurs voitures, depuis aucunes nouvelles de lui a &eacute;t&eacute; signal&eacute;es. D\'apr&egrave;s nos sources, c\'est {joueur} qui l\'a d&eacute;nonc&eacute;.</p>', 11);
INSERT INTO `#__wub_articles` VALUES(21, '<p>A force de trop se foutre de la gueule de {objectif}, {joueur} s\'est fait attrap&eacute; de force dans un coin de {position} par {objectif} qui &eacute;tait un peu fach&eacute; de voir que {joueur} avait essay&eacute; de le voler .... Depuis la r&eacute;putation de {joueur} aupr&egrave;s de ses coll&egrave;gues, est descendu en fl&ecirc;che.</p>', 10);
INSERT INTO `#__wub_articles` VALUES(22, '<p>La section recherche&amp;investigation de la police local se penche de plus en plus s&eacute;rieusement sur la disparition de {objectif}, brave citoyen de notre belle ville.<br />\r\nLa derni&egrave;re personne &agrave; l\'avoir vu affirme qu\'il &eacute;tait en pleine dispute avec {joueur} dans les sous-sol d\'un pavillon en {position}. La police a retrouv&eacute; des traces de sang sur les lieux du crime... La famille de la victime d&eacute;clare que {joueur} devait une &quot;certaine somme d\'argent&quot; &agrave; {objectif}, On nous a dit environs {argent}.</p>', 5);
INSERT INTO `#__wub_articles` VALUES(23, '<p>Des coups de feu ont &eacute;t&eacute; entendu ce soir dans les ruelles de la banlieue. <br />\r\nUn r&eacute;glement de compte entre bande rivale en {position}? Cela semble &ecirc;tre &agrave; l\'origine de l\'incident, les habitants ont pu contempeler &agrave; leur r&eacute;veil le spectacle effroyable des cadavres dont {objectif} gisant le sol. Histoire de drogue ou de prostitu&eacute;es... {joueur} passe l&agrave; un message fort &agrave; ses rivaux.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(24, '<p>C\'est lors d\'un simple controle d\'identit&eacute;e que l\'agent {joueur} est tomb&eacute; en {position} sur 5 gr de L.S.D. dans la banane de  {objectif}. Par la suite la police a decouverte 500 g de h&eacute;roine pendant la fouille anal. He  oui {objectif} l\' a vraiment cette fois-ci dans le cul ...</p>', 8);
INSERT INTO `#__wub_articles` VALUES(25, '<p>{joueur} en a marre des racailles comme {objectif} qui font leur terreur dans le quartier. Du coups {objectif} s\'est fait passer un savon pendant quelques heures !!!  D\'ailleurs, si vous le croisez ne lui parlez pas des pages jaunes. Depuis {joueur} evite le plus possible les environs de {position}.</p>', 8);
INSERT INTO `#__wub_articles` VALUES(26, '<p>La Mafia de {joueur} a encore frapp&eacute; de plus belle sur {objectif} qui n\' a rien vu arriver, la defense de {objectif}  n\' &eacute;tait pas pr&ecirc;te &agrave; une attaque aussi forte de la part de l\' &eacute;quipe de {joueur}. Ils courent encore dans la nature avec {argent}!! La police est sur l\'affaire... Attention a ne pas tra&icirc;ner vers {position}!</p>', 3);
INSERT INTO `#__wub_articles` VALUES(27, '<p>Encore les coffres vides!! Pourquoi {objectif} n\' a pas changer son syst&egrave;me de s&eacute;curit&eacute; depuis la derniere attaque? D\'apr&egrave;s les cam&eacute;ras de surveillance on a pu apercevoir le {joueur} sortant de la banque avec le sourire jusqu aux oreilles... Le butin se monte &agrave; {argent}, {joueur} doit &ecirc;tre aux anges &agrave; l\'heure actuelle.</p>', 3);
INSERT INTO `#__wub_articles` VALUES(28, '<p>Et oui, le grand {objectif} a &eacute;t&eacute; retrouv&eacute; par l\'agent {joueur} dans une rue, les fesses &agrave; l\'air et le visage en sang . {joueur} l\'a ramen&eacute; directement &agrave; l\'h&ocirc;pitale. La seule chose qu\'on sait sur toute cette histoire, c est que {objectif} restera un petit moment derri&egrave;res les barreaux.</p>', 8);
INSERT INTO `#__wub_articles` VALUES(29, '<p>{joueur} a encore fait des siennes en d&eacute;non&ccedil;ant {objectif} en {position}. {objectif} est arriv&eacute; au comissarriat sans dents, la tronche en sang. Comme d\'habitude  {objectif} ne reconnais pas les faits et mais r&eacute;p&egrave;te qu\'il va faire la peau &agrave; {joueur}.</p>', 11);
INSERT INTO `#__wub_articles` VALUES(30, '<p>L\'affreux {objectif} c\'est enfin  fait arreter par le c&eacute;l&egrave;bre {joueur} qui est fiere de sa prise en {position}. Mais les banlieux de Mafiajob sont chaudes depuis que {objectif} n\'est plus la pour calmer les autres joueurs de sont &eacute;quipe.</p>', 8);
INSERT INTO `#__wub_articles` VALUES(31, '<p>{joueur} a encore frapp&eacute; fort!! Les gardes de {objectif} se sont fait litt&eacute;ralement exploxer par les gangsters qui bossent pour {joueur} !!! Ils sont recherch&eacute;s par la police vers la position {position} car le butin est assez impressionnant d\'environs {argent}!!  A suivre ...</p>', 3);
INSERT INTO `#__wub_articles` VALUES(32, '<p>{objectif} s\' est fais retourner par {joueur} dans une petite ruelle en {position} !!!l Pauvre {objectif}, il est rentr&eacute; chez lui &agrave; poil, avec une douleur dans le cul assez s&eacute;v&egrave;re !!! Sacr&eacute; {joueur}, on le reconnait bien l&agrave;!!</p>', 5);
INSERT INTO `#__wub_articles` VALUES(33, '<p>C\'est par la peau des fesses que {objectif} s\'est encore fait ramener au p&eacute;nitencier (&ccedil;a doit faire mal depuis {position}). {joueur} fait toujours bien son travail avec la racaille surtout quand elle s\'appelle {objectif}.</p>', 8);
INSERT INTO `#__wub_articles` VALUES(34, '<p>C\'est en allant travailler comme &agrave; chaque matin que {objectif} s\'est fait prendre le cul par un suspect arm&eacute; qui l\'aurais mis un 1 coup de feu dans la jambe. D\'apr&egrave;s le portrait robot, il s\'agit de {joueur}, il a &eacute;t&eacute; localis&eacute; vers {position}.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(36, '<p>Nous  avons retrouv&eacute; le corps de {joueur} dans une benne &agrave; ordure en {position} &agrave; moiti&eacute; dans le coma, on pense &agrave; combat de rue car {objectif} est venu de lui m&ecirc;me expliquer les faits &agrave; la police.</p>', 6);
INSERT INTO `#__wub_articles` VALUES(37, '<p>{objectif} s\'est fait attaquer par les pros du braquage, c\'est-&agrave;-dire une &eacute;quipe dirig&eacute; par {joueur}.{objectif} a &eacute;t&eacute; enflamm&eacute; &agrave; l\'aide de solvant inconnu et les malfaiteurs se sont enfuit avec le magot qui s\'&eacute;l&egrave;ve &agrave; {argent}. Laissant derri&egrave;re eux un batiment tout en cendre en {position}.</p>', 3);
INSERT INTO `#__wub_articles` VALUES(38, '<p>{objectif} s\'aurait pris plusieurs coups au ventre, mais il reste vivant. Retrouvera t-il sont agresseur? Se vengera t-il? La suite &agrave; venir. N\'oubliez pas que son agresseur est recherche en {position}, regardez dans le tableau, il est peut-&ecirc;tre connu des force de police.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(39, '<p>{objectif} aurait voulu mettre un coup avec son arme &agrave; {joueur} mais {joueur}  a &eacute;t&eacute; plus rapide et lui a demont&eacute; la t&ecirc;te. Un passant en {position} aurait vu {objectif} allong&eacute; au milieu des rats qui commen&ccedil;a &agrave; le grignoter. L\'ambulance est ariver &agrave; temps mais {objectif} est dans un ficheux &eacute;tat.</p>', 1);
INSERT INTO `#__wub_articles` VALUES(41, '<p>Connus pour des histoires foireuses, {joueur} se retrouve apr&egrave;s un braquage loup&eacute;, dans l\'impossibilit&eacute; de r&eacute;gler leurs dettes avec le ca&iuml;d local en {position}. Effectivement {objectif} a un niveau de s&eacute;curit&eacute; sup&eacute;rieur au niveau de comp&eacute;tences des participants au braquage. A mon avis, {joueur} doit avoir les boules d\'avoir fait perdre son temps &agrave; son &eacute;quipe.</p>', 4);
INSERT INTO `#__wub_articles` VALUES(43, '<p>Les agents de la police nationale ont perdu le sommeil depuis le braquage de {objectif} en {position}, dont le bilan a &eacute;t&eacute; tr&egrave;s lourd : des millions de {argent} emport&eacute;s et trois morts. Les enqu&ecirc;tes ont commenc&eacute; par donner leurs fruits. Aujourd&rsquo;hui, les flics ne se trompent plus de pistes d&rsquo;enqu&ecirc;tes. Ils ont pu localiser et traquer le chauffeur qui a conduit les malfrats sur les lieux de l&rsquo;op&eacute;ration. Il s\'agit de {joueur} un petit de la ville de Mafiajob.</p>', 3);
INSERT INTO `#__wub_articles` VALUES(44, '<p>Un responsable a &eacute;t&eacute; tu&eacute; par balles lors du braquage de sa boutique, {date} dans le 13e arrondissement de Mafiajob, a-t-on appris de sources concordantes. Plusieurs malfaiteurs sont entr&eacute;s dans &quot;{objectif}&quot; pour d&eacute;rober la marchandise estim&eacute; &agrave; {argent}. L\'un d\'eux aurait alors fait usage de son pistolet mitrailleur, atteignant le responsable de deux balles dans le thorax.<br />\r\nA leur arriv&eacute;e, les pompiers ont tent&eacute; de secourir le commer&ccedil;ant &acirc;g&eacute; d\'une cinquantaine d\'ann&eacute;es, mais l\'homme est d&eacute;c&eacute;d&eacute; peu apr&egrave;s. L\'enqu&ecirc;te a &eacute;t&eacute; confi&eacute;e &agrave; la brigade criminelle de la police judiciaire.</p>', 3);
INSERT INTO `#__wub_articles` VALUES(45, '<p>{objectif} aurait pu couler une vie heureux en {position}.  Au lieu de &ccedil;a, il est vient d\'&ecirc;tre mit en prison gr&acirc;ce &agrave; sa d&eacute;nonciation par un joueur, d\'apr&egrave;s les derniers t&eacute;moignages il s\'appel {joueur}. Par contre, {joueur} n\'est plus en s&eacute;curit&eacute; car il faudra bien que {objectif} sorte de prison. <br />\r\n{objectif} est en prison depuis le : {date}</p>', 11);
INSERT INTO `#__wub_articles` VALUES(46, '<p>L\'arrestation de {objectif} en situation irr&eacute;guli&egrave;re venu chercher ses deux petits-enfants dans une &eacute;cole du quartier de {position} a tourn&eacute; &agrave; l\'&eacute;meute, mardi apr&egrave;s-midi: la police a fait usage de matraques et r&eacute;pandu des gaz lacrymog&egrave;nes pour le mettre &agrave; terre, {objectif} a eu 2 cotes briss&eacute;es et quelques bleus. Biens&ucirc;r {objectif} est deri&egrave;res les barreaux du p&eacute;nitencier.</p>', 8);
INSERT INTO `#__wub_articles` VALUES(47, '<p>Plus des bandes de jeunes qui viennent jusque dans notre ville pour r&eacute;gler leur compte en {position}, on oublie de parler d\'un fl&eacute;au qui ravage notre beau pays... Les r&eacute;glements de compte entre paysans !! L\'alcool aidant, il existe de plus en plus de combats de rue entre les habitants de zone rurale... Et oui, nous venons de suivre l\'affaire de {joueur}, un jeune paysan qui avait essay&eacute; de violer {objectif} mais comme il n\'accept&eacute; pas ces avances, {objectif} le repoussa mais {joueur} ne le prit pas bien et donc lui a enfonc&eacute; le canon de son flingue dans le cul et tira!</p>', 5);
INSERT INTO `#__wub_articles` VALUES(48, '<p>{joueur} vient d\'acheter une voiture au parking de {position}, cette voiture appartenait &agrave; {objectif}. La transaction a eu lieu &agrave; {date}. {objectif}, va pouvoir bouger son petit boule un peu plus vite, enfin ce n\'est qu\'une occasion rien de tr&egrave;s extraordinaire. Attention c\'est tout de m&ecirc;me une voiture &agrave; {argent}.</p>', 12);
INSERT INTO `#__wub_batiments` VALUES(1739, 11, 1, 'Au marche de la butte', 'Au marché de la butte, est une petite épicerie de quartier. Vous pouvez y acheter de la nourriture et des alcools. (Faites attention si vous y acheter de la viande, car elle pourrait bien venir d\'un corps humain).', 753, 322, 'stand', 'Au_marche_butte.jpg', '', 13, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1740, 15, 18, 'L\'histoire d\'une page', 'Lorsque réapparaît une page manquante du journal de Johnson Wilke Boother, l\'assassin d\'Abraham Lincoln, tout indique que l\'arrière-arrière-grand-père de Bernie a joué un rôle clé dans la conspiration qui a conduit au meurtre de l\'ancien Président américain. Ce restaurant fût le centre de pleins histoires, d\'ailleurs tu peut y procurer des faux papiers a condition de prouver que tu as bien 250 pts d expérience.', 3335, 1210, 'papier', 'KG_cosa_nostra.jpg', '', 250, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1741, 15, 21, 'SPA', 'Vous voici dans la SPA. Pour les amis des bêtes. Ce bâtiment est la pour le plaisir. Tous le monde peut venir faire un coucou a ces pauvres bêtes.', 2699, 17, 'spa', 'spa.jpg', '', 0, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1743, 9, 1, 'Aphrodisia', 'Situé face d une fontaine, le Café Aphrodisia a été fondé en 2007. Le propriétaire peut te fournir de faux papiers, si tu en as besoin mais il n en propose pas au type qui ont moins de 5 pts d expérience!', 1368, 4231, 'papier', 'Aphrodisia.jpg', '', 5, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1749, 5, 1, 'Pigeot telpoto', 'Dans le garage Pigeot telpoto vous pouvez acheter des voitures, ce qui permettra d avancer plus vite sur la carte. Son services après vente est de très bonne qualité. Tous le monde peux venir ici.', 848, 33657, 'voiture', 'telpoto.jpg', '', 1, 0, 0, 7150, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1755, 12, 7, 'Lyonnaisette', 'Banque de vieux, ici sont une bonne partie des retraites de Mafiajob, Toutes les économies d\'une vie misent entre 4 murs... Il y a pas de niveau pour avoir un compte bancaire.', 2395, 3, 'banque', 'Credit_lyonnais.jpg', '', 0, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1756, 1, 9, 'ANPE', 'Vous pouvez ici trouver vos missions. Ce qui vous permet de vous faire de l argent et d améliorer votre personnage. Vous devez avoir au moins 2 pts d expérience pour pouvoir faire des missions.', 929, 226, 'anpe', 'anpe.jpg', '', 1, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1757, 3, 20, 'Les frères Miguel', 'Les frères Miguel est un des garages les plus coté du coin, vous pourrez acheter des voitures de qualité, ce qui vous permettra d\'avancer plus vite sur la carte. C est un garage qui réclame une clientel qui a au moins 25 pts d expérience.', 856, 2200, 'voiture', 'KG_Yakuza.jpg', '', 25, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1758, 23, 21, 'Armurerie Somo', 'Dans cette armurerie vous pourrez acheter des armes qui permettront d\'augmenter votre défense et votre attaque. Cette boutique propose de bons fusils mais aussi d\'autres produits.', 3402, 64420, 'arme', 'Somo.jpg', '', 0, 0, 0, 8250, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1759, 17, 8, 'Jacques Largeron', 'Dans cette armurerie vous pourrez acheter des armes qui permettront d\'augmenter votre défense et votre attaque. Malgré sa devanture cette boutique vends de très bonne armées. Le portier va te demander d avoir au moins 70 pts d expérience pour entrer.', 3398, 81178, 'arme', 'jacques_largeron.jpg', '', 70, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1762, 20, 16, 'Bar le venuzio', 'C est un lieu très classe par son aspect extérieur mais lorsque l\'on gratte un peu l\'on découvre un gigantesque trafic de drogue. Attention le propriétaire connaît bien ton mode il ne propose ses services qu aux joueur ayant une expérience d au moins 30 pts.', 3197, 277, 'papier', 'venuzio.jpg', '', 30, 0, 0, 6655, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1764, 16, 2, 'Maison close', 'La maison close vous permettra de gerer vos filles, cette option n\'est pas encore disponible. En cours de réalisation. en patientant il y a les jeux flash. Mais tous le monde peut y venir.', 723, 2297, 'jeux', 'sexshop.jpg', '', 0, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1765, 7, 17, 'Bar Royal', 'Retrouvez le chanteur en concert acoustique, seul sur scène, sans fards et sans paillettes, dans une simplicité troublante et touchante avec son répertoire qui revisite 30 ans de vie musicale. Les fans apprécieront et les néophytes aussi. La serveuse a son petit copain qui fait de l\'impression papier, il peut te fournir de faux papiers. Mais il ne parle qu aux joueurs de plus de 2 pts d expérience.', 1621, 1068, 'papier', 'Coffee_royal.jpg', '', 2, 0, 0, 5600, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1766, 25, 2, 'Hyppodrome', 'Ce bâtiment un est des plus important de Mafiajob vu que c\'est par ici que l\'on fini le jeu. Cela dis, serez-vous à la hauteur ? Il te faut au moins 1,000 pts pour rentrer, sinon vas voir dans les règles du jeu.', 1130, 6560667, 'jeux', 'hyppodrome.jpg', '', 1000, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1769, 22, 18, 'Bearn & fils', 'Dans le garage Bearn & fils, vous pouvez acheter des voitures, ce qui permettra d\'avancer plus vite sur la carte. Spécialisé dans le tuning, cette boutique offre des véhicules hors du commun. Garage de luxe il te faut au moins 10 pts d expérience. Faut savoir que c\'est ici que la police récupère leurs voitures.', 2973, 1678, 'voiture', 'crisio.jpg', '', 1, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1770, 21, 9, 'Cyber GAMERS', 'OUVERT DE 24h/24 7j/7, pour les arrachés du jeux vidéo, pleins de jeux flash disponibles. Pour connaitre le plaisir des jeux vidéos, il te faut au moins 50 pts d expérience.', 3876, 1509, 'jeux', 'Cyber_coffe.jpg', '', 50, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1775, 1, 5, 'Bus n 1', 'Les tunnels sont des réseau qui vous permet de ressortir vers une autre partie de la carte et donc vous permet de vous déplacer plus vite. Les tunnels sont pour les gens qui ont un niveau d expérience d au moins 20 pts.', 3301, 168, 'tunnel', 'tunnel_1.jpg', '', 20, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1779, 21, 7, 'Barramundada', 'Entre restaurant, bar et club, entre Afrique et Asie, dans un décor à la fois luxueux et moderne-branché, le Barramundada vous propose un voyage ambitieux. On choisira aussi entre les thés du monde et des cocktails d inspirations lointaines.', 2916, 1068, 'papier', 'bieres.jpg', '', 0, 0, 0, 6897, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1781, 16, 4, 'FDO populaire', 'Malgré sa petite porte d\'entrée, cette banque protège les caisses de fond public, ce qui veut dire que c\'est limite une mission commando pour l\'imaginer braquée. Il y a pas de niveau pour avoir un compte bancaire.', 3486, 2254714, 'banque', 'FDO.jpg', '', 0, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1786, 8, 22, 'La cartouche dansante', 'Dans cette armurerie vous trouverez de quoi acheter des armes qui permettront d\'augmenter votre défense et votre attaque. Plusieurs armes de choix vous seront proposées. Tous le monde peut acheter ici!!', 3039, 66031, 'arme', 'KG_Triade.jpg', '', 0, 0, 0, 6655, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1791, 14, 16, 'Yuri & pimp my ride', 'Dans le garage Yuri & pimp my ride vous pouvez acheter des voitures, ce qui permettra d\'avancer plus vite sur la carte. Le propriétaire adore MTV avec son : Pimp My Ride d\'où le nom de la boutique. Vous y trouverez tout de même de bons véhicules. Tu dois avoir au moins 9 pts d expérience.', 3230, 19034, 'voiture', 'Garage_yuri.jpg', '', 9, 0, 0, 8052, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1798, 5, 4, 'Parking municipal', 'Le but d\'un parking est de pouvoir y garer ses voitures...\r\nPour le moment cette option n\'est pas disponible, en cours de réalisation. Tous le monde ne peut pas acheter des occasions comme ça! il faut au moins 30 pts d expérience.\r\nLe reseau des Parkings de Mafiajob vous permet de récupérer ou de ventre sur tous nos points de services (Sur tous les parkings).', 2498, 19481, 'parking', 'parking.jpg', '', 30, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1805, 25, 10, 'BNP', 'Une banque de basic, un coffre, un système de surveillance et des gardes. Cela étant, une petite équipe bien équipée peut y arriver. Il y a pas de niveau pour avoir un compte bancaire.', 3627, 732050, 'banque', 'BNP.jpg', '', 0, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1806, 19, 22, 'ANPE', 'Vous pouvez ici trouver vos missions. Elles vous permettront de gagner un peu d\'argent sans trop prendre de risque. Attention ce bâtiment est pour ceux qui ont une expérience d au moins 3 pts.', 838, 195, 'anpe', 'anpe2.jpg', '', 1, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1811, 2, 2, 'Atlantique banque', 'C\'est un bâtiment bien protégé mais qui contient un coffre de grande valeur. Pour la braquer il vaut mieux s\'y prendre à plusieurs et d\'avoir le bon équipement.', 4265, 7689474, 'banque', 'Banque_atlantique.jpg', '', 0, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1813, 18, 2, 'Le jardin bio', 'Le jardin bio est une petite épicerie de quartier qui a bonne réputation, Comme toute bonne épicerie, tu peux demander des faux papiers. Le propriétaire préfère travailler avec des joueurs qui ont au moins 7 pts d expériences.', 1338, 9744, 'papier', 'KG_mafia_rouge.jpg', '', 7, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1818, 7, 13, 'Pénitencier', 'Le pénitencier, un monde fabuleux pour les bouffons qui se font attaquer. Vous avez la possibilité de leur rendre visite en les voyant sur le tableau ci-dessous. Faut être un bon pour pouvoir y penser!!', 2000, 9778, 'penitencier', 'penitencier.jpg', '', 1500, 0, 0, 6300, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1820, 10, 9, 'Lambrossio', 'Restaurant de spécialité italienne ou vous pourrez les déguster, ce qui vous permettra de reprendre un peu de santé. Seul les grosses pointures peuvent venir ici.', 1188, 2420, 'papier', 'Lambrossio.jpg', '', 500, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1821, 2, 7, 'Armurerie des tireurs', 'Dans cette armurerie vous pourrez acheter des armes qui permettront d\'augmenter votre défense et votre attaque. Le propriétaire a été champion du monde de tir sur cible.', 3136, 125167, 'arme', 'armurerie.jpg', '', 0, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1825, 5, 9, 'Eurosia & Co', 'Une des plus vieilles banques de Mafiajob, mais aussi la plus vieille niveau équipement de protection, cela dit, être seul pour l\'attaquer c\'est un peu sauter d\'un pont sans élastique. Il y a pas de niveau pour avoir un compte bancaire.', 4024, 1317690, 'banque', 'Banque_Eurosia.jpg', '', 0, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1828, 3, 22, 'Credito agricolo', 'Une banque pour le peuple. Elle conserve tous les livret A des jeunes de cette partie de Mafiajob. Mais faut croire que ces jeunes gagnent correctement leur vie. Il y a pas de niveau pour avoir un compte bancaire.', 1474, 1718200, 'banque', 'Credit_agricole.jpg', '', 0, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1838, 2, 24, 'Bus n 2', 'Les tunnels sont des réseau qui vous permet de ressortir vers une autre partie de la carte et donc vous permet de vous déplacer plus vite. Les tunnels sont pour les gens qui ont un niveau d expérience d au moins 20 pts.', 3484, 977, 'tunnel', 'tunnel_2.jpg', '', 20, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1846, 12, 15, 'Casino', 'Casino très classe. Vous pouvez dépenser vos sous dans les machines. Attention de ne pas vous ruiner. N\'oubliez pas que même en jouant à la machine à sous on peut vous attaquer.Etablissement de prestige pour les joueurs de plus de 100 pts d expérience.', 3326, 11308105, 'casino', 'Casino.jpg', '', 100, 0, 0, 6655, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1877, 18, 4, 'Bus n 3', 'Bus sont pour les gens qui ont un niveau d expérience d au moins 1 pts.', 2270, 807, 'tunnel', 'tunnel_3.jpg', '', 1, 0, 0, 5500, 0, 1);
