INSERT INTO `#__wub_batiments` VALUES(1886, 22, 24, 'Bus n 4', 'Les tunnels sont des réseau qui vous permet de ressortir vers une autre partie de la carte et donc vous permet de vous déplacer plus vite. Les tunnels sont pour les gens qui ont un niveau d expérience d au moins 20 pts.', 3040, 1372, 'tunnel', 'tunnel_4.jpg', '', 20, 0, 0, 6050, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1887, 24, 21, 'Circuit de Mafiajob', 'Circuit automobile où s\'affronte les plus grands pilotes de Mafiajob. Le circuit est en cours de construction pour que tous les habitants de Mafiajob puissent y faire tourner leurs voitures. Dès lors que tu aura 400 pts d expérience tu pourras faire des courses.', 1388, 1387, 'circuit', 'circuit.jpg', '', 400, 0, 0, 5000, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1888, 22, 13, 'Hôpital', 'Pour vous soigner de vos petits bobos.', 3905, 37263, 'hospital', 'hospital1.jpg', '', 0, 0, 0, 5500, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1889, 20, 19, 'parking parko', 'Un petit parking bien connu des habitants de Mafiajob. Il y a de bonnes affaires là-bas.\r\nLe reseau des Parkings de Mafiajob vous permet de récupérer ou de ventre sur tous nos points de services (Sur tous les parkings).', 3428, 74, 'parking', 'parking2.jpg', '', 5, 0, 0, 6054, 0, 1);
INSERT INTO `#__wub_batiments` VALUES(1890, 12, 12, 'Parking rouge', 'Un petit parking bien connu des habitants de Mafiajob. Il y a de bonnes affaires là-bas. Tous les prix bas se trouve ici.\r\nLe reseau des Parkings de Mafiajob vous permet de récupérer ou de ventre sur tous nos points de services (Sur tous les parkings).', 2928, 133, 'parking', 'parking3.jpg', '', 10, 0, 0, 6050, 0, 1);
