INSERT INTO `#__wub_voitures` VALUES(43, 'police1.jpg', 100, 30, 'Spécial Police', 'Avec un bolide comme ça, y a des ordures qui vont bouffer de l\'asphalte.', 99, 10, 50, 50, 250, 95, 99, 1769, 1, 500, 1);
INSERT INTO `#__wub_voitures` VALUES(44, 'police2.jpg', 90, 55, 'Spécial Police', 'Une petite corvette pour les flics rien de mieux pour courir après les mafieux!', 89, 10, 50, 50, 250, 10, 88, 1769, 3, 5, 1);
INSERT INTO `#__wub_voitures` VALUES(45, 'police3.jpg', 100, 40, 'Spécial Police', 'Une voiture à l\'américaine, rien de mieux qu\'une corvette pour faire la course.', 93, 10, 50, 50, 250, 60, 96, 1769, 2, 60, 1);
