INSERT INTO `#__wub_voitures` VALUES(49, 'police7.jpg', 100, 35, 'Spécial Police', 'Une petite Porsche ? rien de mieux pour prendre l\'air et pour la chasse aux bouffons.', 94, 10, 50, 50, 250, 80, 95, 1769, 2, 100, 1);
